// ShowKeeper.js
import React from "react"
import "./showkeeper.css"

const ShowKeeper = ({ keeperList, setKeeperList }) => {
    const deleteKeeper = (id) => {
        setKeeperList(prevList => prevList.filter(keeper => keeper._id !== id))
    }

    // Function to chunk the array into groups of size
    const chunkArray = (array, size) => {
        const chunkedArray = []
        for (let i = 0; i < array.length; i += size) {
            chunkedArray.push(array.slice(i, i + size))
        }
        return chunkedArray
    }

    const columns = chunkArray(keeperList, 4)

    return (
        <div className="showKeeper row">
            {columns.map((column, columnIndex) => (
                <div className="col-md-3" key={columnIndex}>
                    {column.map(keeper => (
                        <div className="keeperCard" key={keeper._id}>
                            <h1 className="title">
                                {keeper.title} 
                                <i className="deleteIcon fa fa-trash" aria-hidden="true" onClick={() => deleteKeeper(keeper._id)} ></i>
                            </h1>
                            <textarea 
                                className="descriptionBox" 
                                value={keeper.description} 
                                readOnly 
                            />
                        </div>
                    ))}
                </div>
            ))}
        </div>
    )
}

export default ShowKeeper
