import React from "react"
import "./showkeeper.css"

const ShowKeeper = ({ keeperList, setKeeperList }) => {
    const deleteKeeper = (id) => {
        setKeeperList(prevList => prevList.filter(keeper => keeper._id !== id))
    }
    const chunkArray = (arr, chunkSize) => {
        const chunkedArr = []
        for (let i = 0; i < arr.length; i += chunkSize) {
            chunkedArr.push(arr.slice(i, i + chunkSize))
        }
        return chunkedArr
    }

    // Chunk keeperList into arrays of 4 items each
    const chunkedKeepers = chunkArray(keeperList, 4)

    return (
        <div>
            {chunkedKeepers.map((row, index) => (
                <div className="showKeeper row" key={index}>
                    {row.map(keeper => (
                        <div className="keeperCard col-md-3" key={keeper._id}>
                            <h1 className="title">
                                {keeper.title} 
                                <i className="deleteIcon fa fa-trash" aria-hidden="true" onClick={() => deleteKeeper(keeper._id)} ></i>
                            </h1>
                            <textarea 
                                className="descriptionBox" 
                                value={keeper.description} 
                                readOnly 
                            />
                        </div>
                    ))}
                </div>
            ))}
        </div>
    )
}

export default ShowKeeper
